import { Component, OnInit } from '@angular/core';
import { VentaModel } from 'src/app/core/models/venta.model';
import { VentasService } from 'src/app/core/services/ventas.service';

@Component({
  selector: 'app-ventas',
  templateUrl: './ventas.component.html',
  styleUrls: ['./ventas.component.css'],
})
export class VentasComponent implements OnInit {
  dataSource: VentaModel[] = [];

  displayedColumns: string[] = ['month', 'amountEstate', 'totalSales'];

  constructor(private ventasService: VentasService) {}

  ngOnInit(): void {
    this.ventasService.getAll().subscribe(
      (resp) => {
        this.dataSource = resp.data.items;
      },
      (err) => {
        alert('Something went wrong login.');
      }
    );
  }
}
<div class="middle">
  <h1 class="title">ESTADISTICAS</h1>
  <table
    class="tabla"
    mat-table
    [dataSource]="dataSource"
    class="mat-elevation-z8"
  >
    <!--- Note that these columns can be defined in any order.
      The actual rendered columns are set as a property on the row definition" -->

    <!-- Position Column -->
    <ng-container matColumnDef="month">
      <th mat-header-cell *matHeaderCellDef>Mes</th>
      <td mat-cell *matCellDef="let element">{{ element.month }}</td>
    </ng-container>

    <!-- Name Column -->
    <ng-container matColumnDef="amountEstate">
      <th mat-header-cell *matHeaderCellDef>Nº Inmuebles</th>
      <td mat-cell *matCellDef="let element">{{ element.amountEstate }}</td>
    </ng-container>

    <!-- Weight Column -->
    <ng-container matColumnDef="totalSales">
      <th mat-header-cell *matHeaderCellDef>Total Ventas</th>
      <td mat-cell *matCellDef="let element">{{ element.totalSales }}</td>
    </ng-container>

    <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
    <tr mat-row *matRowDef="let row; columns: displayedColumns"></tr>
  </table>
</div>
